package day18_nestedForLoop_Whileloop;

public class C03_WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	for (int i = 20; i < 30; i++) {
		System.out.print(i + " ");
	}
	System.out.println(" ");
	int sayi=20;
	
	while (sayi<=30);
	System.out.print(sayi  + " ");
	
	sayi++;
	
	
	}

}
