package day19;

public class C04_DoWihileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		while(i<5) {
			System.out.print(i+" ");
		i++;
		}
		
		int a=0;
		do {System.out.print(a+" ");
			a++;
		} while(a<5);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
