package day26_forEachLoop_constructor;

public class C02_forEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int arr[]= {3,4,2,5};
		
		int carpim=1;
		
		for (int each : arr) {
			carpim*=each;
		}
		
		
		System.out.println("arrayin elemanlari carpimi:"+carpim);
		
		
		
		
		
		
		
	}

}
