package day09_ternary;

public class C2_ternary2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int y=1;
	int z=1;
	
	
	int a = y<10 ? y++ : z++;
	
	
	
	System.out.println(y + " ," + z + " ," + a);
	
	System.out.println(y++);
	System.out.println(++y);
	
	System.out.println(y);
	
	
	}

}
